let numero=document.getElementById("numeroMaximo"),btn=document.getElementById("btnSortear"),resultado=document.querySelector(".resultado"),resultadoValor=document.querySelector(".resultado span"),numeroSorteado=1;function sortearNumero(e){return numeroSorteado=Math.round(Math.random()*Number(e))}function exibirNumeroSorteado(e){e.preventDefault(),resultado.style.display="block",resultadoValor.textContent=sortearNumero(numero.value)}btn.addEventListener("click",exibirNumeroSorteado);